 * Toast 显示框
 * @Author: chenliangshan
 * @Date: 2017-12-28 10:44:17
 * @Last Modified by: chenliangshan
 * @Last Modified time: 2017-12-28 10:44:41
 */

<template>
  <transition name="fade">
    <div class="Toast" v-if="toastShow">
      <div class="box" :style="positionTop">
        <span :class="iconClass" :style="iconBg" v-if="iconIsShow"></span>
        <p v-if="message">{{message}}</p>
      </div>
    </div>
  </transition>
</template>

<script>
export default {
  name: 'Toast',
  props: {
    message: { // 提示内容
      type: String,
    },
    toastShow: { // 是否显示
      type: Boolean,
      default: false,
    },
    iconClass: { // 背景图片
      type: String,
    },
    iconImage: { // 自定义背景图片
    },
    duration: { // 定时器
      type: Number,
      default: 1500,
    },
    position: { // 弹出框位置
      type: String,
      default: '50%',
    },
  },
  computed: {
    // 用于判断显示框位置
    positionTop() {
      return {
        top: this.position,
      }
    },
    // 自定义父组件传过来的背景图片
    iconBg() {
      if (this.iconImage) {
        return {
          backgroundImage: `url(${this.iconImage})`,
        }
      }
      return {}
    },
    // 用于判断icon是否显示
    iconIsShow() {
      if (this.iconClass === 'success') {
        return true
      } else if (this.iconClass === 'close') {
        return true
      } else if (this.iconClass === 'warning') {
        return true
      } else if (this.iconImage) {
        return true
      }
      return false
    },
  },
  watch: {
    toastShow() {
      // 监听toast显示，向父组件派发事件
      if (this.toastShow) {
        if (this.duration < 0) {
          this.$emit('toastClose')
        } else {
          setTimeout(() => {
            this.$emit('update:toastShow', false) // 利用了.sync达到双向数据绑定
            this.$emit('toastClose')
          }, this.duration)
        }
      }
    },
  },
}

</script>
<style scoped>
.fade-enter-active {
  transition: all .3s ease;
}

.fade-leave-active {
  transition: all 0 ease;
}

.fade-enter,
.fade-leave-to {
  opacity: 0;
}

.Toast {
  width: 100%;
  height: 100%;
  position: fixed;
  top: 0;
  left: 0;
  background: transparent;
  z-index: 9999;
}

.Toast .box {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 10000;
  max-width: 80%;
  background: rgba(0, 0, 0, 0.7);
  padding: 20px 60px;
  border-radius: 10px;
}

.Toast .box p {
  font-size: 28px;
  color: #fff;
  line-height: 1.5;
  text-align: center;
}

.Toast .box span {
  width: 120px;
  height: 120px;
  display: block;
  margin: 30px auto 20px;
  background: no-repeat center center;
  background-size: 100% 100%;
}

.Toast .box span.success {
  background-image: url('data:image/png;base64,');
}

.Toast .box span.close {
  background-image: url('data:image/png;base64,');
}

.Toast .box span.warning {
  background-image: url('data:image/png;base64,');
}

</style>
